mapper.select:
SELECT 

FROM lic_regcase a
WHERE a.id = #{id}

mapper.sql:
      FROM lic_regcase a
		inner  join  cancer_person_info b on a.MANAGEID = b . ID
		LEFT JOIN LIC_RISK_ASSESSMENT licas ON (a. ID = licas.LIC_CHECK_ID) AND licas.DEL_FLAG='0'
		LEFT JOIN LIC_ASSIST_CHECK licac ON (a. ID = licac.LIC_CHECK_ID) AND licac.DEL_FLAG='0'

			a.del_flag = '0'
			and   b.del_flag = '0'

				AND (
				b.name LIKE	CONCAT(CONCAT('%',#{keyword}),'%')
				OR
				b.personcard LIKE	CONCAT(CONCAT('%',#{keyword}),'%')
				)


				and   a.region_code=#{regarea}


				and   a.regorg=#{regorg}


				and  a.check_year=#{csnf}




						and  a.check_result is null


						and  a.check_result=#{checkResult}




				and  a.close_result=#{closeResult}

mapper.select:


		select * from (


		select * from ( select row_.*, rownum rownum_ from (


        select
			b.personcard_type AS "personcardType",
			b.personcard AS "personcard",
			b.name AS "name",
			b.gender AS "gender",
			licas.ASSESSMENT_RESULT AS "assessmentResult",
			licac.LIC_ASSIST_RESULT AS "licAssistResult",




				ORDER BY ${sortField} ${sortOrder}


				ORDER BY  a.regdate asc




		) row_ where rownum <= #{pageSize}


		) row_ where rownum <= #{endRecord}) where rownum_ > #{startRecord}

mapper.update:
UPDATE lic_regcase SET
id = #{id},manageid = #{manageid},check_year = #{checkYear},check_result = #{checkResult},fzjc_date = #{fzjcDate},region_code = #{regionCode},regorg = #{regorg},regdoc = #{regdoc},regdate = #{regdate},submits_status = #{submitsStatus},submit_date = #{submitDate},trans_date = #{transDate},print_date = #{printDate},close_status = #{closeStatus},close_date = #{closeDate},close_result = #{closeResult},check_quality_flag = #{checkQualityFlag},checkresult_quality_flag = #{checkresultQualityFlag},upload_time = #{uploadTime},source_id = #{sourceId},del_flag = #{delFlag},create_by = #{createBy},create_date = #{createDate}
WHERE id = #{id}
