mapper.select:
SELECT 

FROM crc_regcase a
WHERE a.id = #{id}

mapper.sql:
      FROM crc_regcase a
      inner  join  cancer_person_info b on a.MANAGEID = b . ID

		a.del_flag = '0'
		and   b.del_flag = '0'

			  AND (
			  b.name LIKE	CONCAT(CONCAT('%',#{keyword}),'%')
			  OR
			  b.personcard LIKE	CONCAT(CONCAT('%',#{keyword}),'%')
			  )


			  and   a.region_code=#{regarea}


			  and   a.regorg=#{regorg}


			  and  a.check_year=#{csnf}


			  and  a.check_result=#{crcResult}


			  and  a.close_status=#{closeStatus}

mapper.select:


		select * from (


		select * from ( select row_.*, rownum rownum_ from (


        select
		    b.personcard_type AS "personcardType",b.personcard AS "personcard",b.name AS "name",b.gender AS "gender",




				ORDER BY ${sortField} ${sortOrder}






		) row_ where rownum <= #{pageSize}


		) row_ where rownum <= #{endRecord}) where rownum_ > #{startRecord}

mapper.update:
UPDATE crc_regcase SET
id = #{id},manageid = #{manageid},id_number = #{idNumber},check_year = #{checkYear},check_result = #{checkResult},cbq_return_date1 = #{cbqReturnDate1},cbq_return_date2 = #{cbqReturnDate2},region_code = #{regionCode},regorg = #{regorg},regdoc = #{regdoc},regdate = #{regdate},submits_status = #{submitsStatus},submit_date = #{submitDate},trans_date = #{transDate},print_date = #{printDate},close_status = #{closeStatus},close_date = #{closeDate},close_result = #{closeResult},check_quality_flag = #{checkQualityFlag},checkresult_quality_flag = #{checkresultQualityFlag},upload_time = #{uploadTime},source_id = #{sourceId},ischange = #{ischange},del_flag = #{delFlag},create_by = #{createBy},create_date = #{createDate}
WHERE id = #{id}
