mapper.select:
		select
         p.ID as "id",
		 p.NAME as  "name",
		 p.GENDER as "gender",
		 p.PERSONCARD as "personCard",
		 crc.ID_NUMBER as "idNumber",
		 crc.CHECK_RESULT as "crcResult",
		 crcas.ASSESSMENT_RESULT as "crcAssessmentResult",
		 fobt.FIRST_FOBT_RESULT  as "firstFobtResult",
		 fobt.SECOND_FOBT_RESULT as "secondFobtResult",
		 fobt.fobt_result as "fobtResult",
		 lic.CHECK_RESULT as "licResult",
		 licas.ASSESSMENT_RESULT as  "licAssessmentResult",
		 licc.LIC_ASSIST_RESULT as "licAssistResult",
		 sc.CHECK_RESULT as "scResult",
		 luc.CHECK_RESULT as "lucResult"
from
    CANCER_PERSON_INFO p LEFT JOIN CRC_REGCASE crc  on (crc.MANAGEID=p.ID )
					LEFT JOIN CRC_RISK_ASSESSMENT crcas on (crc.ID = crcas.CRC_CHECK_ID)
					LEFT JOIN CRC_FOBT fobt on (crc.ID = fobt.CRC_CHECK_ID)
					LEFT JOIN LIC_REGCASE lic on (p.ID = lic.MANAGEID)
					LEFT JOIN LIC_RISK_ASSESSMENT licas on (lic.ID = licas.LIC_CHECK_ID)
					LEFT JOIN LIC_ASSIST_CHECK licc on (lic.ID = licc.LIC_CHECK_ID)
					LEFT JOIN SC_REGCASE sc on (p.ID = sc.MANAGEID)
					LEFT JOIN LUC_REGCASE luc  on (p.ID = luc.MANAGEID)
	  where
		p.DEL_FLAG = 0


		and p.id=#{id}


		and  p.create_date=#{csnf}


		and   regarea = #{regarea}


		and   p.regorg=#{regorg}


		and  p.regdate>=to_date(#{beginDateFrom},'yyyy-MM-dd HH:mi:ss')


		  AND b.TS_RQ < to_date(#{tsRqEnd},'yyyy-MM-dd HH:mi:ss')+1


		and  crcAssessmentResult=#{crcAssessmentResult}


		and  firstFobtResult=#{firstFobtResult}


		and  secondFobtResult=#{secondFobtResult}


		and  fobtResult=#{fobtResult}


		and  crcResult=#{crcResult}


		and  licAssessmentResult=#{licAssessmentResult}


		and  licAssistResult=#{licAssistResult}


		and licResult=#{licResult}


		and  scResult=#{scResult}


		and  lucResult=#{lucResult}


	  ORDER BY p.ID DESC

SELECT 

FROM cancer_person_info a
WHERE a.id = #{id}

SELECT

FROM cancer_person_info a
WHERE
a.personcard_type = #{type}
and a.personcard = #{personcardno}
and a.del_flag='0'



		select * from (


		select * from ( select row_.*, rownum rownum_ from (


        select




				ORDER BY ${sortField} ${sortOrder}


				ORDER BY a.update_date DESC




		) row_ where rownum <= #{pageSize}


		) row_ where rownum <= #{endRecord}) where rownum_ > #{startRecord}

mapper.update:
UPDATE cancer_person_info SET
id = #{id},personcard_type = #{personcardType},personcard = #{personcard},name = #{name},gender = #{gender},birth = #{birth},telephone = #{telephone},mobile = #{mobile},nation = #{nation},nation_other = #{nationOther},marriage = #{marriage},education = #{education},payment_situation = #{paymentSituation},profession = #{profession},other_profession = #{otherProfession},paddress_province = #{paddressProvince},paddress_city = #{paddressCity},paddress_county = #{paddressCounty},paddress_town = #{paddressTown},paddress_committee = #{paddressCommittee},paddress_detail = #{paddressDetail},address_province = #{addressProvince},address_city = #{addressCity},address_county = #{addressCounty},address_town = #{addressTown},address_committee = #{addressCommittee},address_detail = #{addressDetail},regorg = #{regorg},regdoc = #{regdoc},regdate = #{regdate},del_flag = #{delFlag},ischange = #{ischange},create_by = #{createBy},create_date = #{createDate},update_by = #{updateBy},update_date = #{updateDate}
WHERE id = #{id}
