mapper.select:
SELECT 

FROM cancer_person_info a
WHERE a.id = #{id}

SELECT

FROM cancer_person_info a
WHERE
a.personcard_type = #{type}
and a.personcard = #{personcardno}
and a.del_flag='0'

mapper.sql:
      FROM cancer_person_info P

			LEFT JOIN CRC_REGCASE crc ON (crc.MANAGEID = P . ID) AND crc.DEL_FLAG='0'

                and   crc.region_code=#{regarea}


                and   crc.regorg=#{regorg}


                and  crc.check_year=#{csnf}


				and  crc.close_status=#{status}


				and  crc.regdate>=to_date(#{beginDateFrom},'yyyy-MM-dd HH:mi:ss')


				AND  crc.regdate < to_date(#{endDateFrom},'yyyy-MM-dd HH:mi:ss')+1

			LEFT JOIN CRC_RISK_ASSESSMENT crcas ON (crc. ID = crcas.CRC_CHECK_ID) AND crcas.DEL_FLAG='0'
			LEFT JOIN CRC_FOBT fobt ON (crc. ID = fobt.CRC_CHECK_ID) AND fobt.DEL_FLAG='0'


			LEFT JOIN LIC_REGCASE lic ON (P . ID = lic.MANAGEID) AND lic.DEL_FLAG='0'

                and   lic.region_code=#{regarea}


                and   lic.regorg=#{regorg}


                and  lic.check_year=#{csnf}


				and  lic.close_status=#{status}


				and  lic.regdate>=to_date(#{beginDateFrom},'yyyy-MM-dd HH:mi:ss')


				AND  lic.regdate < to_date(#{endDateFrom},'yyyy-MM-dd HH:mi:ss')+1

			LEFT JOIN LIC_RISK_ASSESSMENT licas ON (lic. ID = licas.LIC_CHECK_ID) AND licas.DEL_FLAG='0'
			LEFT JOIN LIC_ASSIST_CHECK licac ON (lic. ID = licac.LIC_CHECK_ID) AND licac.DEL_FLAG='0'


			LEFT JOIN SC_REGCASE sc ON (P . ID = sc.MANAGEID)  AND sc.DEL_FLAG='0'

                and   sc.region_code=#{regarea}


                and   sc.regorg=#{regorg}


				and  sc.close_status=#{status}


                and  sc.check_year=#{csnf}


				and  sc.regdate>=to_date(#{beginDateFrom},'yyyy-MM-dd HH:mi:ss')


				AND  sc.regdate < to_date(#{endDateFrom},'yyyy-MM-dd HH:mi:ss')+1



			LEFT JOIN LUC_REGCASE luc ON (P . ID = luc.MANAGEID)  AND luc.DEL_FLAG='0'

                and   luc.region_code=#{regarea}


                and   luc.regorg=#{regorg}


                and  luc.check_year=#{csnf}


				and  luc.close_status=#{status}


				and  luc.regdate>=to_date(#{beginDateFrom},'yyyy-MM-dd HH:mi:ss')


				AND  luc.regdate < to_date(#{endDateFrom},'yyyy-MM-dd HH:mi:ss')+1



		  P .DEL_FLAG = '0'

			  and p.id=#{id}


			  AND (
			  p.name LIKE	CONCAT(CONCAT('%',#{keyword}),'%')
			  OR
			  p.personcard LIKE	CONCAT(CONCAT('%',#{keyword}),'%')
			  )




				  and  crcas.assessment_result=#{crcAssessmentResult}


				  and  fobt.first_fobt_result=#{firstFobtResult}


				  and  fobt.second_fobt_result=#{secondFobtResult}


				  and  fobt.fobt_result=#{fobtResult}


				  and  crc.check_result=#{crcResult}





				  and  licas.assessment_result=#{licAssessmentResult}


				  and  licac.lic_assist_result=#{licAssistResult}


				  and lic.check_result=#{licResult}





				  and  sc.check_result=#{scResult}





				  and  luc.check_result=#{lucResult}

mapper.update:
UPDATE cancer_person_info SET
id = #{id},personcard_type = #{personcardType},personcard = #{personcard},name = #{name},gender = #{gender},birth = #{birth},telephone = #{telephone},mobile = #{mobile},nation = #{nation},nation_other = #{nationOther},marriage = #{marriage},education = #{education},payment_situation = #{paymentSituation},payment_no = #{paymentNo},profession = #{profession},other_profession = #{otherProfession},paddress_province = #{paddressProvince},paddress_city = #{paddressCity},paddress_county = #{paddressCounty},paddress_town = #{paddressTown},paddress_committee = #{paddressCommittee},paddress_detail = #{paddressDetail},address_province = #{addressProvince},address_city = #{addressCity},address_county = #{addressCounty},address_town = #{addressTown},address_committee = #{addressCommittee},address_detail = #{addressDetail},regorg = #{regorg},regdoc = #{regdoc},regdate = #{regdate},del_flag = #{delFlag},ischange = #{ischange},patid=#{patid},blh=#{blh},create_by = #{createBy},create_date = #{createDate},update_by = #{updateBy},update_date = #{updateDate}
WHERE id = #{id}
