mapper.select:
SELECT 

FROM crc_fobt a
WHERE a.id = #{id}



		select * from (


		select * from ( select row_.*, rownum rownum_ from (


        select




				ORDER BY ${sortField} ${sortOrder}






		) row_ where rownum <= #{pageSize}


		) row_ where rownum <= #{endRecord}) where rownum_ > #{startRecord}

mapper.update:
UPDATE crc_fobt SET
id = #{id},crc_check_id = #{crcCheckId},first_fobt_result = #{firstFobtResult},second_fobt_result = #{secondFobtResult},first_fobt_date = #{firstFobtDate},second_fobt_date = #{secondFobtDate},fobt_result = #{fobtResult},fobt_doc = #{fobtDoc},fobt_doc_name = #{fobtDocName},upload_time = #{uploadTime},ischange = #{ischange},del_flag = #{delFlag},create_by = #{createBy},create_date = #{createDate}
WHERE id = #{id}
