mapper.select:
SELECT 

FROM crc_risk_assessment a
WHERE a.id = #{id}



		select * from (


		select * from ( select row_.*, rownum rownum_ from (


        select




				ORDER BY ${sortField} ${sortOrder}






		) row_ where rownum <= #{pageSize}


		) row_ where rownum <= #{endRecord}) where rownum_ > #{startRecord}

mapper.update:
UPDATE crc_risk_assessment SET
id = #{id},crc_check_id = #{crcCheckId},manxingfuxie = #{manxingfuxie},manxingbianmi = #{manxingbianmi},nianyexuebian = #{nianyexuebian},lanweiyan = #{lanweiyan},dannangyan = #{dannangyan},jingshenchuangshang = #{jingshenchuangshang},aizhengshi = #{aizhengshi},qinshu_aizhengshi = #{qinshuAizhengshi},changxirou = #{changxirou},xiyan = #{xiyan},xuexichong = #{xuexichong},ganrannian = #{ganrannian},assessment_result = #{assessmentResult},assessment_doc = #{assessmentDoc},assessment_doc_name = #{assessmentDocName},assessment_date = #{assessmentDate},risk_quality_flag = #{riskQualityFlag},upload_time = #{uploadTime},ischange = #{ischange},del_flag = #{delFlag},create_by = #{createBy},create_date = #{createDate}
WHERE id = #{id}
