mapper.select:
SELECT 

FROM family_cancer_history a
WHERE a.id = #{id}



		select * from (


		select * from ( select row_.*, rownum rownum_ from (


        select




				ORDER BY ${sortField} ${sortOrder}


				ORDER BY a.update_date DESC




		) row_ where rownum <= #{pageSize}


		) row_ where rownum <= #{endRecord}) where rownum_ > #{startRecord}

mapper.update:
UPDATE family_cancer_history SET
id = #{id},check_id = #{checkId},relation = #{relation},cancer_type = #{cancerType},age = #{age},lived = #{lived},del_flag = #{delFlag},ischange = #{ischange},create_by = #{createBy},create_date = #{createDate},update_by = #{updateBy},update_date = #{updateDate}
WHERE id = #{id}
