mapper.select:
SELECT 

FROM lic_assist_check a
WHERE a.id = #{id}



		select * from (


		select * from ( select row_.*, rownum rownum_ from (


        select




				ORDER BY ${sortField} ${sortOrder}






		) row_ where rownum <= #{pageSize}


		) row_ where rownum <= #{endRecord}) where rownum_ > #{startRecord}

mapper.update:
UPDATE lic_assist_check SET
id = #{id},lic_check_id = #{licCheckId},gtp = #{gtp},hbeg = #{hbeg},afp = #{afp},bus = #{bus},lic_assist_result = #{licAssistResult},lic_assist_doc = #{licAssistDoc},lic_assist_doc_name = #{licAssistDocName},lic_assist_date = #{licAssistDate},upload_time = #{uploadTime},del_flag = #{delFlag},create_by = #{createBy},create_date = #{createDate}
WHERE id = #{id}
