mapper.select:
SELECT 

FROM lic_regcase a
WHERE a.id = #{id}



		select * from (


		select * from ( select row_.*, rownum rownum_ from (


        select




				ORDER BY ${sortField} ${sortOrder}






		) row_ where rownum <= #{pageSize}


		) row_ where rownum <= #{endRecord}) where rownum_ > #{startRecord}

mapper.update:
UPDATE lic_regcase SET
id = #{id},manageid = #{manageid},check_year = #{checkYear},check_result = #{checkResult},fzjc_date = #{fzjcDate},region_code = #{regionCode},regorg = #{regorg},regdoc = #{regdoc},regdate = #{regdate},submits_status = #{submitsStatus},submit_date = #{submitDate},trans_date = #{transDate},print_date = #{printDate},close_status = #{closeStatus},close_date = #{closeDate},close_result = #{closeResult},check_quality_flag = #{checkQualityFlag},checkresult_quality_flag = #{checkresultQualityFlag},upload_time = #{uploadTime},source_id = #{sourceId},del_flag = #{delFlag},create_by = #{createBy},create_date = #{createDate}
WHERE id = #{id}
