mapper.select:
SELECT 

FROM lic_risk_assessment a
WHERE a.id = #{id}



		select * from (


		select * from ( select row_.*, rownum rownum_ from (


        select




				ORDER BY ${sortField} ${sortOrder}






		) row_ where rownum <= #{pageSize}


		) row_ where rownum <= #{endRecord}) where rownum_ > #{startRecord}

mapper.update:
UPDATE lic_risk_assessment SET
id = #{id},lic_check_id = #{licCheckId},yigan = #{yigan},binggan = #{binggan},ganyinghua = #{ganyinghua},aizhengshi = #{aizhengshi},qinshu_aizhengshi = #{qinshuAizhengshi},assessment_result = #{assessmentResult},assessment_doc = #{assessmentDoc},assessment_doc_name = #{assessmentDocName},assessment_date = #{assessmentDate},risk_quality_flag = #{riskQualityFlag},upload_time = #{uploadTime},del_flag = #{delFlag},create_by = #{createBy},create_date = #{createDate}
WHERE id = #{id}
