mapper.select:
SELECT 

FROM luc_app_diag_xh a
WHERE a.id = #{id}



		select * from (


		select * from ( select row_.*, rownum rownum_ from (


        select




				ORDER BY ${sortField} ${sortOrder}


				ORDER BY a.update_date DESC




		) row_ where rownum <= #{pageSize}


		) row_ where rownum <= #{endRecord}) where rownum_ > #{startRecord}

mapper.update:
UPDATE luc_app_diag_xh SET
id = #{id},manageid = #{manageid},app_diag_check_code = #{appDiagCheckCode},app_diag_check_date = #{appDiagCheckDate},app_diag_check_time = #{appDiagCheckTime},app_diag_check_ksdm = #{appDiagCheckKsdm},app_diag_check_ksmc = #{appDiagCheckKsmc},app_diag_check_ysdm = #{appDiagCheckYsdm},app_diag_check_ysxm = #{appDiagCheckYsxm},app_diag_check_hx = #{appDiagCheckHx},appointment_doc = #{appointmentDoc},appointment_org = #{appointmentOrg},appointment_date = #{appointmentDate},del_flag = #{delFlag},create_by = #{createBy},create_date = #{createDate},update_by = #{updateBy},update_date = #{updateDate}
WHERE id = #{id}
