mapper.select:
SELECT 

FROM luc_app_ldct_xh a
WHERE a.id = #{id}



		select * from (


		select * from ( select row_.*, rownum rownum_ from (


        select




				ORDER BY ${sortField} ${sortOrder}


				ORDER BY a.update_date DESC




		) row_ where rownum <= #{pageSize}


		) row_ where rownum <= #{endRecord}) where rownum_ > #{startRecord}

mapper.update:
UPDATE luc_app_ldct_xh SET
id = #{id},manageid = #{manageid},app_check_code = #{appCheckCode},app_check_date = #{appCheckDate},app_check_time = #{appCheckTime},appointment_doc = #{appointmentDoc},appointment_org = #{appointmentOrg},appointment_date = #{appointmentDate},del_flag = #{delFlag},create_by = #{createBy},create_date = #{createDate},update_by = #{updateBy},update_date = #{updateDate}
WHERE id = #{id}
