mapper.select:
SELECT 

FROM luc_pushrisk_xh a
WHERE a.id = #{id}



		select * from (


		select * from ( select row_.*, rownum rownum_ from (


        select




				ORDER BY ${sortField} ${sortOrder}


				ORDER BY a.update_date DESC




		) row_ where rownum <= #{pageSize}


		) row_ where rownum <= #{endRecord}) where rownum_ > #{startRecord}

mapper.update:
UPDATE luc_pushrisk_xh SET
id = #{id},personcard = #{personcard},risk_account = #{riskAccount},risk_comment1 = #{riskComment1},risk_comment2 = #{riskComment2},del_flag = #{delFlag},source_id = #{sourceId},create_by = #{createBy},create_date = #{createDate},update_by = #{updateBy},update_date = #{updateDate}
WHERE id = #{id}
